const intialState = {
    list:[]
}
const todoList=(state=intialState, action)=>{
switch(action.type){
    case "ADD": 
    const {id,inputtodo} = action.payload
    return{
        ...state,
        list:[
            ...state.list,
            {
            id:id,
            inputtodo:inputtodo
        }]
    }
    default: return state
}
}
export  default todoList
