import {todosRef} from "../auth/FirebaseAuth"

export const addTodo =(inputtodo)=>{

    return{
        type:"ADD",
        payload:{
            id: new Date().getTime().toString(),
            inputtodo:inputtodo 
        }

    }
    todosRef.push().set()
}
export const deleteTodo = (id)=>{
    // todosRef.child(completeToDo).remove();
    return{
        type:"DELETE",
        id

    }
}
