import React, { useState } from 'react'
import "./input.css"
import AddIcon from '@material-ui/icons/Add';
import { useDispatch, useSelector } from 'react-redux';
import { addTodo, deleteTodo } from '../actions/index'
import DeleteIcon from '@material-ui/icons/Delete';

const Input = () => {
  const [inputTodo, setInputTodo] = useState("")
  // for todolist
  const list = useSelector((state) => state.todoList.list)
  const dispatch = useDispatch()
  return (<>
    <div className="add_item">
      <input type="text" placeholder="✍️ Add your new todo..." value={inputTodo} onChange={(event) => { setInputTodo(event.target.value) }} />
      <a className="plus_btn" onClick={() => dispatch(addTodo(inputTodo), setInputTodo(""))}><AddIcon /> </a>
    </div>
    {/* todo list display with delete button */}
    <div className="list_items">
    {
      list.map((element)=>{
        <div className="item" key={element.id}>
        <h3>{element.data}</h3>
        <a className="delet_btn" onClick={() => dispatch(deleteTodo(element.id))}><DeleteIcon /></a>
      </div>
      })
    }

    </div>

  </>
  )
}

export default Input
