import React,{useState} from 'react'
import "./input.css"
import AddIcon from '@material-ui/icons/Add';
import { useDispatch, useSelector } from 'react-redux';
import{addTodo, deleteTodo} from '../actions/index'
import DeleteIcon from '@material-ui/icons/Delete';

const Input = () => {
  const [inputTodo, setInputTodo] = useState("")
  const list = useSelector((state)=> state.todoList.list)
  const dispatch = useDispatch()
    return (<>
        <div className="add_item">
          <input type="text" placeholder="✍️ Add your new todo..." value={inputTodo} onChange={(event)=>{setInputTodo(event.target.value)}} />
          <a className="plus_btn" onClick={()=>dispatch(addTodo(inputTodo),setInputTodo(""))}><AddIcon  /> </a>
        </div>

        <div className="list_items">
        <div className="item">
            <h3>test</h3>
         <a className="delet_btn" onClick={()=>dispatch(deleteTodo(inputTodo),setInputTodo(""))}><DeleteIcon/></a>
        </div>



        </div>

        </>
    )
} 

export default Input
